import React from "react";
import "./style.css";

function Form (props) {

    return (
      <div>
       <h1>Employee Directory</h1>

       <div class="entertext">
        <form className="form">

          <input
            value={props.firstName}
            name="firstName"
            onChange={props.handleInputChange}
            type="text"
            placeholder="First Name or Last Name (lowercase only!)"
          />

          <button onClick={props.handleFormSubmit}>Submit</button>
        </form>
        </div>
      </div>
    );

}

export default Form;
